import React,{useState,useEffect} from 'react';
import clsx from 'clsx';
import {Link} from 'react-router-dom';
import { makeStyles, useTheme } from '@material-ui/core/styles';
import Drawer from '@material-ui/core/Drawer';
import CssBaseline from '@material-ui/core/CssBaseline';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import Divider from '@material-ui/core/Divider';
import IconButton from '@material-ui/core/IconButton';
import MenuIcon from '@material-ui/icons/Menu';
import ChevronLeftIcon from '@material-ui/icons/ChevronLeft'; 
import {ChromePicker} from 'react-color';
import Button from '@material-ui/core/Button';
import DragableColorList from './DragableColorList';
import {arrayMove} from 'react-sortable-hoc';

// import { ValidatorForm, TextValidator } from 'react-material-ui-form-validator';

const drawerWidth = 400;

const useStyles = makeStyles((theme) => ({
  root: {
    display: 'flex',
  },
  appBar: {
    transition: theme.transitions.create(['margin', 'width'], {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen,
    }),
  },
  appBarShift: {
    width: `calc(100% - ${drawerWidth}px)`,
    marginLeft: drawerWidth,
    transition: theme.transitions.create(['margin', 'width'], {
      easing: theme.transitions.easing.easeOut,
      duration: theme.transitions.duration.enteringScreen,
    }),
  },
  menuButton: {
    marginRight: theme.spacing(2),
  },
  hide: {
    display: 'none',
  },
  drawer: {
    width: drawerWidth,
    flexShrink: 0,
  },
  drawerPaper: {
    width: drawerWidth,
  },
  drawerHeader: {
    display: 'flex',
    alignItems: 'center',
    padding: theme.spacing(0, 1),
    // necessary for content to be below app bar
    ...theme.mixins.toolbar,
    justifyContent: 'flex-end',
  },
  content: {
    flexGrow: 1, 
    height:`calc(${100}vh-${64}px)`,
    padding: theme.spacing(3),
    transition: theme.transitions.create('margin', {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen,
    }),
    marginLeft: -drawerWidth,
  },
  contentShift: {
    transition: theme.transitions.create('margin', {
      easing: theme.transitions.easing.easeOut,
      duration: theme.transitions.duration.enteringScreen,
    }),
    marginLeft: 0,
  },
}));

function NewPaletteForm(props) {
    console.log(props);
    const classes = useStyles();
    const theme = useTheme();
    const [open, setOpen] = useState(false);
    const [colors,setColors]=useState(props.palettes[0].colors)
    const [currentcolor,setCurrent]=useState("teal");
    const [newColorName,setnewColorName]=useState("");
    const [newPaletteName,setnewPaletteName]=useState("");
    const paletteFull=colors.length>=20;
    const handleDrawerOpen = () => {
        setOpen(true);
    };

    const handleDrawerClose = () => {
        setOpen(false);
    };

    const updateCurrentColor=(newcolor)=>{
        setCurrent(newcolor.hex);
    }
    const addnewColors=()=>{
        const newcolor={
            name:newColorName,
            color:currentcolor  
        }
        setColors([...colors,newcolor]);
        setnewColorName("");
    }

    const handleChange1=(evt)=>{
        setnewColorName(evt.target.value);
    }
    const handleChange2=(evt)=>{
        //console.log(evt);
        setnewPaletteName(evt.target.value);
    }
    const savePalette=()=>{
        let newName=newPaletteName;
        const newPalette={

            paletteName:newName,
            id:newName.toLowerCase().replace(/ /g,"-"),
            colors:colors
        };
        props.savePalette(newPalette); 
        props.history.push("/");
    }

    const handleNewName=()=>{
        setnewPaletteName(newPaletteName);
    }
    const removeColor=(colorName)=>{
        const remaining=colors.filter(color=>color.name!==colorName);
         setColors(remaining)
    }
    // const onSortEnd=(oldIndex,newIndex)=>{
    //     const newcs=arrayMove(colors,oldIndex,newIndex)
    //     setColors(({colors})=>({colors:newcs}))
    // }
    const onSortEnd = ({ oldIndex, newIndex }) => setColors(arrayMove(colors, oldIndex, newIndex));
    const clearPalette=()=>{
        setColors([]);
    }

    const addRandom=()=>{
        const allColors = props.palettes.map(p => p.colors).flat();

        let rand = Math.floor(Math.random() * allColors.length);
        let randomColor = allColors[rand];

        setColors([...colors, randomColor] );
    }
    // useEffect(()=>{
    //     ValidatorForm.addValidationRule("isColorNameUnique",value=>
    //         color.every(
    //             ({name})=>name.toLowerCase()!==value.toLowerCase()
    //         )
    //     );
    //     ValidatorForm.addValidationRule("isColorUnique",value=>
    //             ({color})=>color!=currentcolor
    //     );
    // }
    //)
    return (
        <div className={classes.root}>
      <CssBaseline />
      <AppBar
        position="fixed"
        className={clsx(classes.appBar, {
          [classes.appBarShift]: open,
        })}
      >
        <Toolbar>
          <IconButton
            color="inherit"
            aria-label="open drawer"
            onClick={handleDrawerOpen}
            edge="start"
            className={clsx(classes.menuButton, open && classes.hide)}
          >
            <MenuIcon />
          </IconButton>
          <Typography variant="h6" noWrap>
            Persistent drawer
          </Typography>
          <form onSubmit={savePalette}>
          <input value={newPaletteName} onChange={handleChange2} name='newPaletteName'></input>
          <Button 
            variant='contained'  
            color='primary' type='submit'>Save Palette</Button>

            <Link to="/">
            <Button variant='contained'  
            color='secondary' >Go Back</Button>
            </Link>
            </form>

        </Toolbar>
      </AppBar>
      <Drawer
        className={classes.drawer}
        variant="persistent"
        anchor="left"
        open={open}
        classes={{
          paper: classes.drawerPaper,
        }}
      >
        <div className={classes.drawerHeader}>
          <IconButton onClick={handleDrawerClose}>
             <ChevronLeftIcon /> 
          </IconButton>
        </div>
        <Divider />
        <Typography variant='h4'>Design your Palette</Typography>

        <div>
            <Button variant='contained' color='secondary' onClick={clearPalette}>Clear Palette</Button>
            <Button variant='contained' color='primary' onClick={addRandom}>Random Color</Button>

        </div>
        <ChromePicker color={currentcolor} onChangeComplete={(newcolor)=>updateCurrentColor(newcolor)}/>
        {/* <ValidatorForm>
        <TextValidator value={newName} onChange={handleChange}/>

        </ValidatorForm> */}
        {/* <form onSubmit={addnewColors}> */}
        <input value={newColorName} onChange={handleChange1} name='newColorName'/>
        <Button 
            variant='contained' 
            //  type='submit' 
            onClick={addnewColors}
            color='primary' 
            style={{background:paletteFull?"grey":currentcolor}} 
            disabled={paletteFull}>
            {paletteFull?"Palette Full":"Add Color"}
            </Button>
        {/* </form> */}
      </Drawer>
      <main
        className={clsx(classes.content, {
          [classes.contentShift]: open,
        })}
      >
        <div className={classes.drawerHeader} /> 
         <DragableColorList 
            colors={colors} 
            removeColor={removeColor}
            axis='xy'
            onSortEnd={onSortEnd}
            />
      </main>
    </div>
    )
}

export default NewPaletteForm
